_M={}function _M.parse_sprite(a)local b={}b.width=string.byte(a,1)<<8|string.byte(a,2)b.height=string.byte(a,3)<<8|string.byte(a,4)b.bpp=string.byte(a,5)b.num_colors=string.byte(a,6)b.palette_data=string.sub(a,7,7+b.num_colors*3-1)b.pixel_data=string.sub(a,7+b.num_colors*3)return b end;function _M.set_palette(c,d)local e={'VOID','WHITE','GREY','RED','PINK','DARKBROWN','BROWN','ORANGE','YELLOW','DARKGREEN','GREEN','LIGHTGREEN','NIGHTBLUE','SEABLUE','SKYBLUE','CLOUDBLUE'}for f=1,c do local g=(f-1)*3;frame.display.assign_color(e[f],string.byte(d,g+1),string.byte(d,g+2),string.byte(d,g+3))end end;return _M